import React from 'react';

class Button extends React.Component {
    constructor() {
        super();
        this.state = {
            count: 0
        };
    };

    clickHandler = () => {
        console.log(this.state);
        return this.setState = () => {this.state++};
    };

    render () {
        return(
            <section>
                <h4>Counter</h4>
                <button onClick={this.clickHandler}>+</button>
                <button>-</button>
            </section>
        );
    };
};

export default Button;
